package breakContinue;

public class BreakContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int x = 0; x < 10; x++)
	    {
	        
	        
	        if(x==4)
	        {
	            continue;
	            
	        }
	        System.out.println(x);
	    }

		
		
		
	}

}
